mapper.insert:
insert into food(code, name,
maker, material, servingWt, calory, carbo, protein, fat, sugar,
natrium, chole, fattyacid, transfat, year, img)
values(#{code:INTEGER}
,#{name:VARCHAR}
,#{maker:VARCHAR}
,#{material:VARCHAR}
,#{servingWt:INTEGER}
,#{calory:DOUBLE}
,#{carbo:DOUBLE}
,#{protein:DOUBLE}
,#{fat:DOUBLE}
,#{sugar:DOUBLE}
,#{natrium:DOUBLE}
,#{chole:DOUBLE}
,#{fattyacid:DOUBLE}
,#{transfat:DOUBLE}
,#{year:INTEGER}
,#{img:VARCHAR}
)
