mapper.insert:
		insert into myfood(id, code, quantity,date)
		values(#{id:VARCHAR}
   		        ,#{code:INTEGER}
   		        ,#{quantity:INTEGER}
   		        ,#{date:VARCHAR}
   		       )

mapper.select:
select id, code, quantity, name, maker, material, img
from myfood
left join food
using (code)
where id=#{id} and code=#{code}
